"use strict";document.querySelector(".js-form").addEventListener("submit",function(e){e.preventDefault()}),document.querySelectorAll(".js-tab").forEach(e=>{e.addEventListener("click",function(){const t=e.dataset.content;document.querySelectorAll(".js-tab").forEach(e=>{e.classList.remove("settings-tabs__item--active")}),e.classList.add("settings-tabs__item--active"),document.querySelectorAll(".js-tab-content").forEach(e=>{e.dataset.content===t?e.classList.add("active"):e.classList.remove("active")})})});const rangeInputs=document.querySelectorAll('input[type="range"]'),cancel=document.querySelector(".js-cancel");function resetRange(){rangeInputs.forEach(e=>{e.style.backgroundSize="50% 100%"}),document.querySelectorAll(".js-range").forEach(function(e){const t=e.querySelector('input[type="range"]'),n=e.querySelector(".js-range-output");setTimeout(()=>{n.textContent=t.value},0)})}function handleInputChange(e){var e=e.target,t=e.min,n=e.max,c=e.value;e.closest(".js-range").querySelector(".js-range-output").textContent=c,e.style.backgroundSize=100*(c-t)/(n-t)+"% 100%"}cancel.addEventListener("click",resetRange),rangeInputs.forEach(e=>{e.addEventListener("input",handleInputChange)}),document.querySelector(".js-copy").addEventListener("click",()=>{var e=document.querySelector(".js-copy-content").textContent;navigator.clipboard.writeText(e)});